const fs = require\(''fs''\);
const path = require\(''path''\);

const ROOT = __dirname;
const SKIP = new Set\([''node_modules'', ''.git'', ''.vercel'', ''images'', ''css'', ''js'']\);

function walk\(dir\) {
  let results = [];
  for \(const entry of fs.readdirSync\(dir, { withFileTypes: true }\)\) {
    const fullPath = path.join\(dir, entry.name\);
    if \(entry.isDirectory\(\) && !SKIP.has\(entry.name\)\) {
      results = results.concat\(walk\(fullPath\)\);
    } else if \(entry.isFile\(\) && entry.name.endsWith\(''.html''\)\) {
      results.push\(fullPath\);
    }
  }
  return results;
}

function getJsPath\(filePath\) {
  const rel = path.relative\(ROOT, filePath\).split\(path.sep\).join\(''/''\);
  const depth = rel.split\(''/''\).length - 1;
  if \(depth === 0\) return ''js/'';
  if \(depth === 1\) return ''../js/'';
  if \(depth === 2\) return ''../../js/'';
  return ''../''.repeat\(depth\) + ''js/'';
}

const allFiles = walk\(ROOT\);
console.log\(''Found '' + allFiles.length + '' HTML files''\);

let modified = 0;
let skipped = 0;

for \(const filePath of allFiles\) {
  const rel = path.relative\(ROOT, filePath\).split\(path.sep\).join\(''/''\);
  if \(rel === ''index.html''\) { console.log\(''SKIP: '' + rel\); skipped++; continue; }

  let content = fs.readFileSync\(filePath, ''utf8''\);
  const original = content;

  // Check for footer
  const footerRe = new RegExp\(''<footer[\\s\\S]*?<\\/footer>'', ''gi''\);
  if \(!footerRe.test\(content\)\) { console.log\(''SKIP \(no footer\): '' + rel\); skipped++; continue; }

  // Replace footer
  content = content.replace\(new RegExp\(''<footer[\\s\\S]*?<\\/footer>'', ''gi''\), ''<footer></footer>''\);

  // Remove year script blocks
  content = content.replace\(new RegExp\(''<script>[\\s\\S]*?getElementById\\s*\\\\\(\\s*[\\x27\\x22]year[\\x27\\x22]\\s*\\\\\)[\\s\\S]*?<\\/script>'', ''gi''\), ''''\);

  // Clean blank lines
  content = content.replace\(/\\n\\s*\\n\\s*\\n/g, ''\\n\\n''\);

  const jsPath = getJsPath\(filePath\);
  const footerTag = ''<script src="' + jsPath + 'footer.js"></script>'';

  if \(!content.includes\(''footer.js''\)\) {
    const scriptRe = new RegExp\(''\(<script\\s+src=[\\x22\\x27][^\\x22\\x27]*script\\.js[\\x22\\x27]\\s*><\\/script>\)'', ''i''\);
    if \(scriptRe.test\(content\)\) {
      content = content.replace\(scriptRe, ''$1\\n    '' + footerTag\);
    } else {
      const sTag = ''<script src="' + jsPath + 'script.js"></script>'';
      content = content.replace\(''</body>'', ''    '' + sTag + ''\\n    '' + footerTag + ''\\n</body>''\);
    }
  }

  if \(content !== original\) {
    fs.writeFileSync\(filePath, content, ''utf8''\);
    console.log\(''UPDATED: '' + rel\);
    modified++;
  } else {
    console.log\(''SKIP \(no changes\): '' + rel\);
    skipped++;
  }
}

console.log\(''Done! Modified: '' + modified + '', Skipped: '' + skipped\);
''@")
